const fs = require('fs');
const path = require('path');

// Базовый пустой JPEG, который будет использоваться для всех изображений
const base64JpegData = '';

// Функция для создания директорий, если они не существуют
function ensureDirExists(dirPath) {
  if (!fs.existsSync(dirPath)) {
    fs.mkdirSync(dirPath, { recursive: true });
  }
}

// Функция для создания изображения
function createImage(filePath) {
  const buffer = Buffer.from(base64JpegData, 'base64');
  fs.writeFileSync(filePath, buffer);
  console.log(`Created: ${filePath}`);
}

// Создаем все нужные директории
ensureDirExists('public/images');
ensureDirExists('public/images/categories');
ensureDirExists('public/images/products');
ensureDirExists('public/images/promotions');

// Создаем изображения для фонов
for (let i = 1; i <= 5; i++) {
  createImage(`public/images/sushi-background-${i}.jpg`);
}

// Создаем изображения для CTA
for (let i = 1; i <= 3; i++) {
  createImage(`public/images/cta-background-${i}.jpg`);
}

// Создаем изображения для категорий
['rolls', 'sushi', 'sets', 'drinks'].forEach(category => {
  createImage(`public/images/categories/${category}.jpg`);
});

// Создаем изображения для продуктов
['california', 'philadelphia', 'philadelphia-set', 'salmon'].forEach(product => {
  createImage(`public/images/products/${product}.jpg`);
});

// Создаем изображения для акций
['first-order', 'sets-promo'].forEach(promo => {
  createImage(`public/images/promotions/${promo}.jpg`);
});

// Создаем дополнительные изображения
createImage(`public/images/default-product.jpg`);
createImage(`public/images/promo-1.jpg`);
createImage(`public/images/promo-2.jpg`);

console.log('All images created successfully!'); 